      out.write("\n");
      out.write("            // Dynamic Face Recognition windows (loaded from DB)\n");
      out.write("            const FACE_WINDOWS = JSON.parse('");
      out.print( faceWinJson.toString() );
      out.write("');\n");
      out.write("            function withinAllowedWindow(d) {\n");
      out.write("                const jsDay = d.getDay(); // 0=Sun\n");
      out.write("                const dayNorm = jsDay === 0 ? 7 : jsDay; // 1..7\n");
      out.write("                const minutesNow = d.getHours()*60 + d.getMinutes();\n");
      out.write("                return FACE_WINDOWS.some(w => {\n");
      out.write("                    if (w.day !== dayNorm) return false;\n");
      out.write("                    const [sh, sm, ss] = (''+w.start).split(':');\n");
      out.write("                    const [eh, em, es] = (''+w.end).split(':');\n");
      out.write("                    const startM = parseInt(sh,10)*60 + parseInt(sm,10);\n");
      out.write("                    const endM = parseInt(eh,10)*60 + parseInt(em,10);\n");
      out.write("                    return minutesNow >= startM && minutesNow < endM;\n");
      out.write("                });\n");
      out.write("            }\n");
