      out.write("                        coords = await new Promise((resolve, reject) => {\n");
      out.write("                            navigator.geolocation.getCurrentPosition(pos => resolve(pos.coords), err => resolve(null), { enableHighAccuracy: true, timeout: 10000 });\n");
      out.write("                        });\n");
