      out.write("                    const h = faceVideo.videoHeight || 240;\n");
      out.write("                    faceCanvas.width = w; faceCanvas.height = h;\n");
      out.write("                    const ctx = faceCanvas.getContext('2d');\n");
      out.write("                    ctx.drawImage(faceVideo, 0, 0, w, h);\n");
